package com.api.exception;import com.webpos.common.PayType;public class UnOpenOfPayException  extends RuntimeException{  private static final long serialVersionUID = 7095942583639918854L;  private PayType payType;    public UnOpenOfPayException(PayType payType)  {    this.payType = payType;  }    public String getMessage()  {    return String.format("%s接口未启用", new Object[] { this.payType.getDesc() });  }}