package com.api.exception;import com.webpos.common.PayRoute;import com.webpos.common.PayType;public class UnSupportedException  extends RuntimeException{  private static final long serialVersionUID = 7095942583639918854L;  private PayRoute payRoute;  private PayType payType;    public UnSupportedException(PayRoute payRoute, PayType payType)  {    this.payType = payType;    this.payRoute = payRoute;  }    public String getMessage()  {    return String.format("%s不支持%s渠道", new Object[] { this.payRoute.getDesc(), this.payType.getDesc() });  }}