package com.webpos.entity;import com.webpos.tools.CommUtil;import java.io.Serializable;import java.text.DecimalFormat;import java.util.Date;public class Details  implements Serializable{  private static final long serialVersionUID = 1L;  private Long id;  private String user_id;  private String parent_id;  private String short_id;  private String type;  private String remark;  private Date ctime;  private String result;  private String balance;  private String temp_amount;  private String temp_amount_100;    public String getTemp_amount()  {    return this.result.replaceAll("-", "");  }    public void setTemp_amount(String temp_amount)  {    this.temp_amount = temp_amount;  }    public String getTemp_amount_100()  {    double x = 0.0D;    String y = this.result.replaceAll("-", "");    x = CommUtil.mul(y, Double.valueOf(0.01D));    DecimalFormat df = new DecimalFormat("0.000000");        return df.format(x);  }    public void setTemp_amount_100(String temp_amount_100)  {    this.temp_amount_100 = temp_amount_100;  }    public String getBalance()  {    return this.balance;  }    public String getParent_id()  {    return this.parent_id;  }    public void setParent_id(String parent_id)  {    this.parent_id = parent_id;  }    public String getShort_id()  {    return this.short_id;  }    public void setShort_id(String short_id)  {    this.short_id = short_id;  }    public void setBalance(String balance)  {    this.balance = balance;  }    public Long getId()  {    return this.id;  }    public void setId(Long id)  {    this.id = id;  }    public String getUser_id()  {    return this.user_id;  }    public void setUser_id(String user_id)  {    this.user_id = user_id;  }    public String getType()  {    return this.type;  }    public void setType(String type)  {    this.type = type;  }    public String getRemark()  {    return this.remark;  }    public void setRemark(String remark)  {    this.remark = remark;  }    public Date getCtime()  {    return this.ctime;  }    public void setCtime(Date ctime)  {    this.ctime = ctime;  }    public String getResult()  {    return this.result;  }    public void setResult(String result)  {    this.result = result;  }}