package com.webpos.entity;import java.io.Serializable;import java.util.Date;//商品表public class Goods implements Serializable {	private static final long serialVersionUID = 1L;	private Long id;	private String name;//商品名	private Date ctime;	private Double price;//商品价格	private Double nowprice;//现在价格	private int beishu;//倍数	private String detail;//商品详情	private String img;//商品图片	private String status;//状态。正常，禁用	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public Date getCtime() {		return ctime;	}	public void setCtime(Date ctime) {		this.ctime = ctime;	}	public Double getPrice() {		return price;	}	public void setPrice(Double price) {		this.price = price;	}	public Double getNowprice() {		return nowprice;	}	public void setNowprice(Double nowprice) {		this.nowprice = nowprice;	}	public int getBeishu() {		return beishu;	}	public void setBeishu(int beishu) {		this.beishu = beishu;	}	public String getDetail() {		return detail;	}	public void setDetail(String detail) {		this.detail = detail;	}	public String getImg() {		return img;	}	public void setImg(String img) {		this.img = img;	}	public String getStatus() {		return status;	}	public void setStatus(String status) {		this.status = status;	}}