package com.webpos.entity;import java.io.Serializable;import java.util.Date;//商品表public class Info implements Serializable {	private static final long serialVersionUID = 1L;	private Long id;	private String name;//商品名	private Date ctime;	private Long userid;	private String phone;	private String address;		private String cardname;	private String cardno;	private String cardbank;	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public Date getCtime() {		return ctime;	}	public void setCtime(Date ctime) {		this.ctime = ctime;	}	public Long getUserid() {		return userid;	}	public void setUserid(Long userid) {		this.userid = userid;	}	public String getPhone() {		return phone;	}	public void setPhone(String phone) {		this.phone = phone;	}	public String getAddress() {		return address;	}	public void setAddress(String address) {		this.address = address;	}	public String getCardname() {		return cardname;	}	public void setCardname(String cardname) {		this.cardname = cardname;	}	public String getCardno() {		return cardno;	}	public void setCardno(String cardno) {		this.cardno = cardno;	}	public String getCardbank() {		return cardbank;	}	public void setCardbank(String cardbank) {		this.cardbank = cardbank;	}	}