package com.webpos.entity;import java.io.Serializable;import java.util.Date;public class Message implements Serializable {	private static final long serialVersionUID = 1L;	private Long id;	private String userid;	private Integer roomid;	private String message;	private Date ctime;	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getUserid() {		return userid;	}	public void setUserid(String userid) {		this.userid = userid;	}	public Integer getRoomid() {		return roomid;	}	public void setRoomid(Integer roomid) {		this.roomid = roomid;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public Date getCtime() {		return ctime;	}	public void setCtime(Date ctime) {		this.ctime = ctime;	}	}